import React, { useEffect, useState } from "react";
import axios from "axios";
import MapComponent from "../../MapComponent/MapComponent";
import TestData from "../../MapComponent/TestData.json";
import ListComponent from "../../ListComponent/ListComponent";

const baseURL =
  "https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/all_hour.geojson";

const Home = () => {
  const [post, setPost] = useState(null);
  const [value, setValue] = useState("all");
  const [timeFrame, setTimeFrame] = useState("hour");

  function handleChangeMagnitude(event) {
    setValue(event.target.value);
  }

  React.useEffect(() => {
    axios.get(baseURL).then((response) => {
      setPost(response.data.features);
    });
  }, [value, timeFrame]);

  if (!post) return null;

  return (
    <>
      <div>
        <button className="button button1" onClick={() => setTimeFrame("hour")}>
          Past Hour
        </button>
        <button className="button button2" onClick={() => setTimeFrame("day")}>
          Past Day
        </button>
        <button className="button button3" onClick={() => setTimeFrame("week")}>
          Past 7 Days
        </button>
        <button
          className="button button4"
          onClick={() => setTimeFrame("month")}
        >
          Past 30 Days
        </button>
        <div className="dropdownElement">
          <button>
            Magnitude
            <select
              className="dropdown"
              value={value}
              onChange={handleChangeMagnitude}
            >
              <option value="All">All</option>
              <option value="4.5+">4.5+</option>
              <option value="2.5+">2.5+</option>
              <option value="1+">1+</option>
            </select>
          </button>
        </div>
      </div>
      <section className="homeImgScrollableList">
        <div className="homeMapImg"></div>
        <div className="scrollable">
          {isShown && (
            <div>
              {data.map((el) => {
                const { ids, mag, title, detail } = el.properties;
                return (
                  <ListComponent
                    ids={ids}
                    mag={mag}
                    title={title}
                    detail={detail}
                  />
                );
              })}
            </div>
          )}
        </div>
      </section>
      <MapComponent earthquakes={TestData} height={450} width={700} />
    </>
  );
};

export default Home;
