import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const DetailsPage = () => {
  const [post, setPost] = useState([]);

  const navigate = useNavigate();

  React.useEffect(() => {
    axios
      .get("https://earthquake.usgs.gov/earthquakes/feed/v1.0/geojson.php")
      .then((response) => {
        setPost(response.data.features.properties.detail);
      });
  }, []);

  if (!post) return null;

  return (
    <>
      <div className="details">
        <button className="buttonBack" onClick={() => navigate(-1)}>
          {" "}
          {"<= Go Back"}
        </button>
        <h1>EarthQuake Details:</h1>
      </div>
      <div className="containerLabel">
        {post.map((el) => {
          const { title, place, time, status, tsunami } = el.properties;
          return (
            <>
              <h2 className="post-title">title={title}</h2>
              <h2 className="post-place">place={place}</h2>
              <h2 className="post-time">time={time}</h2>
              <h2 className="post-status">status={status}</h2>
              <h2 className="post-tsunami">tsunami={tsunami}</h2>
            </>
          );
        })}
      </div>
    </>
  );
};

export default DetailsPage;
