import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const DetailsPage = () => {
  const [post, setPost] = useState(null);

  const navigate = useNavigate();

  React.useEffect(() => {
    axios
      .get("https://earthquake.usgs.gov/earthquakes/feed/v1.0/geojson.php")
      .then((response) => {
        setPost(response.data.features);
      });
  }, []);

  if (!post) return null;

  return (
    <>
      <div className="details">
        <button className="buttonBack" onClick={() => navigate(-1)}>
          {" "}
          {"<= Go Back"}
        </button>
        <h1>EarthQuake Details:</h1>
      </div>
      <div className="containerLabel">
        {post.map((post) => {
          <div>
            <b>Title</b>
            {post.properties.title}
          </div>;

          // <>
          //   <h2 className="post-title">title={post?.properties?.title}</h2>
          //   <h2 className="post-place">place={post?.properties?.place}</h2>
          //   <h2 className="post-time">time={post?.properties?.time}</h2>
          //   <h2 className="post-status">status={post?.properties?.status}</h2>
          //   <h2 className="post-tsunami">
          //     tsunami={post?.properties?.tsunami}
          //   </h2>
          // </>
        })}
      </div>
    </>
  );
};

export default DetailsPage;
