import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const DetailsPage = () => {
  const [post, setPost] = useState(null);
  const [value, setValue] = useState("");
  const navigate = useNavigate();

  React.useEffect(() => {
    axios
      .get("https://earthquake.usgs.gov/earthquakes/feed/v1.0/geojson.php")
      .then((response) => {
        setPost(response.data.features.properties.detail);
      });
  }, [value]);

  if (!post) return null;

  return (
    <>
      <div className="details">
        <button className="buttonBack" onClick={() => navigate(-1)}>
          {" "}
          {"<= Go Back"}
        </button>
        <h1>EarthQuake Details:</h1>
      </div>
      <div className="containerLabel">
        <label>
          Title
          <select value={title}></select>
        </label>
        <label>
          Place
          <select value={place}></select>
        </label>
        <label>
          Time
          <select value={time}></select>
        </label>
      </div>
    </>
  );
};

export default DetailsPage;
