import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const DetailsPage = () => {
  const [post, setPost] = useState(null);
  const [value, setValue] = useState("");
  const navigate = useNavigate();

  React.useEffect(() => {
    axios
      .get("https://earthquake.usgs.gov/earthquakes/feed/v1.0/geojson.php")
      .then((response) => {
        setPost(response.data.features.properties.detail);
      });
  }, [value]);

  if (!post) return null;

  return (
    <>
      <div className="details">
        <button className="buttonBack" onClick={() => navigate(-1)}>
          {" "}
          {"<= Go Back"}
        </button>
        <h1>EarthQuake Details:</h1>
      </div>
      <div className="containerLabel">
        {post.map((el) => {
          const { title, place, time, status, tsunami } = el.properties;
          return (
            <>
              <label>
                Title
                <select title={title}></select>
              </label>
              <label>
                Place
                <select place={place}></select>
              </label>
              <label>
                Time
                <select time={time}></select>
              </label>
              <label>
                Status
                <select status={status}></select>
              </label>
              <label>
                Tsunami Risk
                <select tsunami={tsunami}></select>
              </label>
            </>
          );
        })}
      </div>
    </>
  );
};

export default DetailsPage;
