import React, { useEffect, useState } from "react";
const Home = () => {
  const [data, setData] = useState([]);
  const [value, setValue] = useState("Any");
  const [isShown, setIsShown] = useState(false);

  function handleChangeMagnitude(event) {
    setValue(event.target.value);
  }

  async function fetchData() {
    setIsShown(false);

    if (value === "Any") {
      const response = await fetch(
        "https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/all_hour.geojson"
      );
      const result = await response.json();
      setData(result.features);
    } else if (value === "4.5+") {
      const response = await fetch(
        "https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/4.5_hour.geojson"
      );
      const result = await response.json();
      setData(result.features);
    } else if (value === "2.5+") {
      const response = await fetch(
        "https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/2.5_hour.geojson"
      );
      const result = await response.json();
      setData(result.features);
    } else if (value === "1+") {
      const response = await fetch(
        "https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/1.0_hour.geojson"
      );
      const result = await response.json();
      setData(result.features);
    }
  }
  useEffect(() => {
    fetchData();
  }, [value]);

  const fetchData2 = async () => {
    setIsShown(false);

    if (value === "Any") {
      const response = await fetch(
        "https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/all_day.geojson"
      );
      const result = await response.json();
      setData(result.features);
    } else if (value === "4.5+") {
      const response = await fetch(
        "https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/4.5_day.geojson"
      );
      const result = await response.json();
      setData(result.features);
    } else if (value === "2.5+") {
      const response = await fetch(
        "https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/2.5_day.geojson"
      );
      const result = await response.json();
      setData(result.features);
    } else if (value === "1+") {
      const response = await fetch(
        "https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/1.0_day.geojson"
      );
      const result = await response.json();
      setData(result.features);
    }
  };
  useEffect(() => {
    fetchData2();
  }, [value]);

  const fetchData3 = async () => {
    setIsShown(false);

    if (value === "Any") {
      const response = await fetch(
        "https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/all_week.geojson"
      );
      const result = await response.json();
      setData(result.features);
    } else if (value === "4.5+") {
      const response = await fetch(
        "https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/4.5_week.geojson"
      );
      const result = await response.json();
      setData(result.features);
    } else if (value === "2.5+") {
      const response = await fetch(
        "https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/2.5_week.geojson"
      );
      const result = await response.json();
      setData(result.features);
    } else if (value === "1+") {
      const response = await fetch(
        "https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/1.0_week.geojson"
      );
      const result = await response.json();
      setData(result.features);
    }
  };
  useEffect(() => {
    fetchData3();
  }, [value]);

  const fetchData4 = async () => {
    setIsShown(false);

    if (value === "Any") {
      const response = await fetch(
        "https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/all_month.geojson"
      );
      const result = await response.json();
      setData(result.features);
    } else if (value === "4.5+") {
      const response = await fetch(
        "https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/4.5_month.geojson"
      );
      const result = await response.json();
      setData(result.features);
    } else if (value === "2.5+") {
      const response = await fetch(
        "https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/2.5_month.geojson"
      );
      const result = await response.json();
      setData(result.features);
    } else if (value === "1+") {
      const response = await fetch(
        "https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/1.0_month.geojson"
      );
      const result = await response.json();
      setData(result.features);
    }
  };
  useEffect(() => {
    fetchData4();
  }, [value]);

  const handleClick = (event) => {
    setIsShown(true);
  };

  return (
    <>
      <div className="buttonSection">
        <div className="navButtons">
          <button
            onClick={() => {
              fetchData();
              handleClick();
            }}
          >
            Past Hour
          </button>
          <button
            onClick={() => {
              fetchData2();
              handleClick();
            }}
          >
            Past Day
          </button>
          <button
            onClick={() => {
              fetchData3();
              handleClick();
            }}
          >
            Past 7 Days
          </button>
          <button
            onClick={() => {
              fetchData4();
              handleClick();
            }}
          >
            Past 30 Days
          </button>
        </div>
        <div className="dropdown">
          <label>
            Magnitude
            <select
              className="selectDropdown"
              value={value}
              onChange={handleChangeMagnitude}
            >
              <option value="Any">Any</option>
              <option value="4.5+">4.5+</option>
              <option value="2.5+">2.5+</option>
              <option value="1+">1+</option>
            </select>
          </label>
        </div>
      </div>

      <section className="homeImgScrollableList">
        <div className="homeMapImg"></div>
      </section>
    </>
  );
};

export default Home;
